mapper.select:
SELECT 

FROM opa_leadership_assessment a

WHERE a.id = #{id}

SELECT
,SUM( a.DEMOCRATIC_ASSESSMENT+ a.SOCIAL_ASSESSMENT+ a.GERMANY_ASSESSMENT+ a.CONVERSATION_ASSESSMENT+ a.NORMAL_ASSESSMENT+ a.DEPARTMENT_ASSESSMENT )as allscore FROM opa_leadership_assessment a JOIN sys_opaleadershipassessment_role ur ON ur.opaLeadershipassessment_id =a.id and ur.role_id=a.ITEM_PARENT_ID
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND (o.id = #{office.id} OR o.parent_ids LIKE '%,'||#{office.id}||',%') '%,'+#{office.id}+',%') CONCAT('%,', #{office.id}, ',%'))
${sqlMap.dsf}
ORDER BY ${page.orderBy}
group by name order by allscore desc

SELECT
,SUM( a.DEMOCRATIC_ASSESSMENT+ a.SOCIAL_ASSESSMENT+ a.GERMANY_ASSESSMENT+ a.CONVERSATION_ASSESSMENT+ a.NORMAL_ASSESSMENT+ a.DEPARTMENT_ASSESSMENT )as allscore FROM opa_leadership_assessment a JOIN sys_opaleadershipassessment_role ur ON ur.opaLeadershipassessment_id =a.id and ur.role_id=a.ITEM_PARENT_ID
a.del_flag = #{DEL_FLAG_NORMAL} and a.user_id=#{user}
ORDER BY ${page.orderBy}
group by name order by allscore desc

SELECT 

FROM opa_leadership_assessment a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.insert:
INSERT INTO sys_opaleadershipassessment_role(opaLeadershipassessment_id, role_id)

	SELECT #{id}, #{role.id}

	FROM dual

INSERT INTO opa_leadership_assessment(ITEM_PARENT_ID)

	SELECT #{role.id}

	FROM dual

mapper.update:
update opa_leadership_assessment set

 ITEM_PARENT_ID=#{role.id}

where id=#{id}

UPDATE opa_leadership_assessment SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT a.id AS "id", a.office_id AS "office.id", a.item_parent_id AS "itemParentId", a.code AS "code", a.name AS "name", a.create_by AS "createBy.id", a.create_date AS "createDate", a.update_by AS "updateBy.id", a.update_date AS "updateDate", a.remarks AS "remarks", a.del_flag AS "delFlag", a.democratic_assessment AS "democraticAssessment", a.social_assessment AS "socialAssessment", a.germany_assessment AS "germanyAssessment", a.conversation_assessment AS "conversationAssessment", a.department_assessment AS "departmentAssessment", a.normal_assessment AS "normalAssessment", a.status AS "status", ifnull(SUM( a.DEMOCRATIC_ASSESSMENT + a.SOCIAL_ASSESSMENT + a.GERMANY_ASSESSMENT + a.CONVERSATION_ASSESSMENT + a.NORMAL_ASSESSMENT + a.DEPARTMENT_ASSESSMENT ),"")AS allscore, IFNULL(a.user_id,"") AS userId, e.name AS "office.name" FROM opa_leadership_assessment a LEFT JOIN sys_office e ON e.id=a.office_id
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
group by name order by allscore desc
