mapper.select:
SELECT 

FROM opa_scheme_item a

WHERE a.id = #{id}

SELECT 
	IFNULL(Max(a.level),0)
FROM opa_scheme_item a
WHERE  
a.del_flag = #{DEL_FLAG_NORMAL}

	AND a.scheme_id = #{schemeId}

SELECT 

FROM opa_scheme_item a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.scheme_id = #{schemeId}


		AND a.item_parent_id = #{itemParentId}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.type = #{type}


		AND a.method = #{method}


		AND a.if_num = #{ifNum}


		AND a.date_from >= #{dateFrom}


		AND a.date_to <= #{dateTo}


		AND a.status = #{status}

	AND a.remarks not in (-1,-2,-3)

ORDER BY a.sort ASC

SELECT 

FROM opa_scheme_item a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.scheme_id = #{schemeId}


	AND a.item_parent_ids like concat('0','%')

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.type = #{type}


		AND a.method = #{method}


		AND a.if_num = #{ifNum}


		AND a.date_from >= #{dateFrom}


		AND a.date_to <= #{dateTo}


		AND a.status = #{status}

	AND a.remarks not in (-1,-2,-3)

GROUP BY a.name

SELECT 

FROM opa_scheme_item a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM opa_scheme_item a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.scheme_id = #{schemeId}


		AND a.item_parent_id = #{itemId}


ORDER BY a.sort ASC

mapper.update:
UPDATE opa_scheme_item SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT 

FROM opa_scheme_item a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.scheme_id = #{schemeId}


		AND a.status != #{status}

mapper.update:
UPDATE opa_scheme_item SET 	
	status = #{status}
WHERE scheme_id = #{schemeId}

mapper.select:
SELECT 

FROM opa_scheme_item a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.scheme_id = #{schemeId}


		AND a.auditor_id = #{auditorId}


		AND a.status = #{status}


 ORDER BY a.level ASC

SELECT 
	IFNULL(SUM(a.value),0) AS childSum
FROM opa_scheme_item a

	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.scheme_id = #{schemeId}


		AND a.item_parent_id = #{itemId}
